import { browserOrNode } from '@aws-amplify/core';
var getDefaultAdapter = function () {
    var isBrowser = browserOrNode().isBrowser;
    if (isBrowser && window.indexedDB) {
        return require('../indexeddb').default;
    }
    var AsyncStorageAdapter = require('../asyncstorage').AsyncStorageAdapter;
    return new AsyncStorageAdapter();
};
export default getDefaultAdapter;
